jQuery(function(){
	tabs.init();
});	
tabs = {
	init : function(){
		jQuery('.tabs').each(function(){
			jQuery(this).find('.tab-content').hide();
			jQuery(jQuery(this).find('ul.nav .selected a').attr('href')).fadeIn(300);
			jQuery(this).find('ul.nav a').click(function(){
				jQuery(this).parents('.tabs').find('.tab-content').hide();
				jQuery(jQuery(this).attr('href')).fadeIn(300);
				jQuery(this).parent().addClass('selected').siblings().removeClass('selected');
				//Cufon.refresh();
				return false;
			});
		});
	}
}